package JavaTest;

public class StringIncrease {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String msg = "the";
		int i;
		for(i=0;i<msg.length();i+=1) {
			System.out.print(msg.substring(i,i+1));
			System.out.print(msg.substring(i,i+1));
			System.out.print(msg.substring(i,i+1));
		}
		
			
	}

}
