
public class SwitchTest {
	
	

	public void checkday(String day) {
		// TODO Auto-generated method stub

	

		
		switch(day) {
	case "Mon":System.out.println("its monday");break;
	case "Tue":	System.out.println("its chewsday");break;
	case "Wed":System.out.println("its wetsdat");break;
	case "Thu":System.out.println("its turdsday");break;
	case "Fri":System.out.println("its fryday");break;
	case "Sat":System.out.println("its shaturday");break;
	case "Sun":System.out.println("its johhhnnnnyyyy");break;
	
	default:System.out.println("what day is it?");
	}
	
		
	}

}
